const fs = require("node:fs"); // apartir de node 16: se recomienda poner node:

const stats = fs.statSync("./archivo.txt");

console.log(
  stats.isFile(), // devuelve true si es un archivo
  stats.isDirectory(), //devuelve true si es una  carpeta
  stats.isSymbolicLink(), //devuelve true si es un  enlace simbólico
  stats.size, // tamaño del archivo o directorio
);
